select * from people 
	where SchoolId > $SchoolId and  ( Name = $Name and Status = $Status){'hiddenby' = '$Name,$Status'}
	//如果$Name,$Status任何一个为空，那么第一个and后的参数化查询恒成立（等于看不见了）

select top 100 * from Sys_WF_Meta
	where (Creator = [Person.Cqy]
	or ( Description = $Description and GraphicMeta = $GraphicMeta){'hiddenby'='$GraphicMeta+$Description'}){'hiddenby'='$GraphicMeta'}
